class network(tnn.Module):
    """
    Class for creating the neural network.  The input to your network will be a
    batch of reviews (in word vector form).  As reviews will have different
    numbers of words in them, padding has been added to the end of the reviews
    so we can form a batch of reviews of equal length.  Your forward method
    should return an output for both the rating and the business category.
    """
    def __init__(self):
        super(network, self).__init__()
        self.num_ngrams = 5000 #number of ngrams that will be used - most optimal first
        factor = (self.num_ngrams/6.)**(1./3.)
        num_hid1 = round(self.num_ngrams/factor) #computes the number of hidden nodes
        num_hid2 = round(6.*factor) #computes the number of hidden nodes
#        print(self.num_ngrams, num_hid1, num_hid2,6)

        # construct ngram matrix
        self.ngrams_vectors=[]
        for i in range(self.num_ngrams):
            word = wordVectors['dummy'].to(device)
            for ngram in optimal_ngrams[i].split():
                word = word + wordVectors[ngram].to(device)
            word = word-wordVectors['dummy'].to(device)
#            word = word / len(optimal_ngrams[i].split())  #divide by the number of vectors
#            self.ngrams_vectors.append(word)
            self.ngrams_vectors.append(word.unsqueeze(0))
        # defines a large matrix to be used to store ngram data
        self.input_matrix = torch.empty([batchSize,self.num_ngrams],dtype=torch.float).to(device)  #32 is equal to batch

	# here is the definition of the linear layers (hidden and out) of the net
        self.in_hid1 = torch.nn.Linear(self.num_ngrams, num_hid1).to(device)
        self.in_hid2 = torch.nn.Linear(num_hid1, num_hid2).to(device)
        self.hid_out = torch.nn.Linear(num_hid2, 6).to(device) #output to 6 nodes: 5 for categorization + 1 for rating

    def forward(self, input, length):
        threshold = 0.90  # cosine similarity cut-off between ngrams and wordvectors, used to populate matrix
        tensor_size = list(input.size())
        num_tokens = tensor_size[1]

        for b in range(batchSize):
#            print('batch:',b)

            for w in range(self.num_ngrams):
                self.input_matrix[b, w] = float(0.0)

            for i in range(num_tokens):
                # construct & test unigram
                y = input[b, i, :]
                dist = float(-1)
                pos = 0
                for w in range(self.num_ngrams):
                    x = self.ngrams_vectors[w]
#                    d = float(torch.norm(x-y).to(device))
                    d = float(torch.cosine_similarity(x, y.unsqueeze(0).to(device)))
#                    if d<dist:
                    if d > dist:
                        dist = d
                        pos = w
                if dist > threshold:
                    self.input_matrix[b, pos] += dist
#                    print(dist)

                # construct & test bigram
                if i < (num_tokens-1):
                    y = input[b, i, :]+input[b, i+1, :]
                    dist = float(-1)
                    pos = 0
                    for w in range(self.num_ngrams):
                        x = self.ngrams_vectors[w]
#                        d = float(torch.norm(x-y).to(device))
                        d = float(torch.cosine_similarity(x, y.unsqueeze(0)).to(device))
                        if d > dist:
                            dist = d
                            pos = w
                    if dist > threshold:
                        self.input_matrix[b,pos] += dist
#                        print(dist)

                # construct & test trigram
                if i< (num_tokens-2):
                    y = input[b, i, :]+input[b, i+1, :]+input[b, i+2, :]
                    dist = float(-1)
                    pos = 0
                    for w in range(self.num_ngrams):
                        x = self.ngrams_vectors[w]
#                        d = float(torch.norm(x- y).to(device))
                        d = float(torch.cosine_similarity(x, y.unsqueeze(0)).to(device))

                        if d > dist:
                            dist = d
                            pos = w
                    if dist > threshold:
                        self.input_matrix[b,pos] += dist
#                        print(dist)

#        print(torch.sum(self.input_matrix).to(device))
        # the loss functions for the neural net
        hid_sum1 = self.in_hid1(self.input_matrix)
        hidden1  = torch.tanh(hid_sum1).to(device)
        hid_sum2 = self.in_hid2(hidden1)
        hidden2  = torch.tanh(hid_sum2).to(device)
        out_sum = self.hid_out(hidden2)
        category =torch.softmax(out_sum[:,:5],dim=1).to(device)
        rating = torch.sigmoid(out_sum[:,5]).to(device)
        return rating, category

class loss(tnn.Module):
    """
    Class for creating the loss function.  The labels and outputs from your
    network will be passed to the forward method during training.
    """

    def __init__(self):
        super(loss, self).__init__()
        self.rating_loss = tnn.BCELoss().to(device)
        self.category_loss = tnn.CrossEntropyLoss().to(device)

    def forward(self, ratingOutput, categoryOutput, ratingTarget, categoryTarget):
        rat_loss = self.rating_loss(ratingOutput.float(), ratingTarget.float())
        cat_loss = self.category_loss(categoryOutput.float(), categoryTarget)
        tot_loss = rat_loss + cat_loss

        return tot_loss

net = network().to(device)
lossFunc = loss().to(device)

